$activePage = 'messages';

$cacheVersion = '10.2.0';
$pageCSS = [
    '/messages/css/messages.css?v=' . $cacheVersion,
    '/messages/css/messages-enhanced.css?v=' . $cacheVersion
];

$pageJS = [
    '/messages/js/messages.js?v=' . $cacheVersion,
    '/messages/js/messages-enhanced.js?v=' . $cacheVersion
];

